import {SEARCH_MOVIE, GET_MOVIES} from './types';
import axios from 'axios';

import {APIKeyOwn} from '../APIKey';

export const searchMovie = text => dispatch => {
    dispatch({
        type: SEARCH_MOVIE,
        payload: text
    });
};

// export const getMovies = text => dispatch => {
