export const getMovies = text => dispatch => {
    axios
      .get(`https://www.omdbapi.com/?apikey=${APIKeyOwn}&s=${text}`)
      .then(response =>
        dispatch({
          type: GET_MOVIES,
          payload: response.data.Search
        })
      )
      .catch(err => console.log(err));
  };
