import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { getOmdb } from '../../actions/searchActions';
// import {connect} from 'react-redux'
import MovieCard from './MovieCard';

export const MoviesContainer = () => {

        const [movies, setMovies] = useState([]);
        const [page, setPage] = useState(1);
        const moviesRdx = useSelector(props => props.movies.movies);
        const searchMoviesRdx = useSelector(props => props.movies.text);
        const dispatch = useDispatch(null);
        useEffect(() => {
            window.addEventListener('scroll', handleScroll);
        }, []);
        useEffect(() => {
            setMovies(moviesRdx);
        }, [moviesRdx]);
        const handleScroll = async () => {
            if (
                Math.ceil(window.innerHeight + document.documentElement.scrollTop) !== document.documentElement.offsetHeight
            )
            return;
            // console.log('inner height', window.innerHeight);
            // console.log('top', document.documentElement.scrollTop);
            // console.log('offset', document.documentElement.offsetHeight);
            await dispatch(getOmdb(
                {page: page, s: searchMoviesRdx}
            ))
            setPage(page+1);
        };

        // const {movies} = this.props;
        let content = null;
        content = typeof movies !== 'undefined' ? movies.map((x, index) => 
            <MovieCard key={index} movie={x}/>
        ) : <div className="text-center mt-3" style={{ margin: '0 auto' }}>Tolong ketikan nama film yang benar pada kolom pencarian</div>;
        return (
            <div className="row">
                {content}
            </div>
        )
}
