import React, { Component } from 'react'
import {searchMovie, getMovies, setLoading } from '../../actions/searchActions'
import  {connect} from 'react-redux' 

export class SearchInput extends Component {

    onChange = e => {
        this.props.searchMovie(e.target.value);
    }

    onSubmit = e => {
        e.preventDefault();
        this.props.getMovies(this.props.text);
        this.props.setLoading();
    }

    render() {
        return (
            <div className=" jumbotron-fluid mt-5 text-center">
            <div className="container">
            <form id="searchForm"
                onSubmit={this.onSubmit}
            >
                <input
                type="text"
                className="form-control"
                name="searchText"
                placeholder="What are you searching for ..."
                onChange={this.onChange}
                />
                <button type="submit" className="bbtn btn-primary btn-lg btn-block mt-3">
                Search
                </button>
            </form>
            </div>
        </div>
        )
    }
}

const mapStateToProps = state => ({
    text: state.movies.text
  });

export default connect(
    mapStateToProps,
    {searchMovie, getMovies, setLoading})
(SearchInput);
