import {SEARCH_MOVIE} from './types';
import axios from 'axios';
import {APIKey} from '../APIKey';

export const searchMovie = text => dispatch => {
    dispatch({
        type: SEARCH_MOVIE,
        payload: text
    });
};

export const getMovies = text => dispatch => {
    axios.get('http://www.omdbapi.com/?apikey=[yourkey]&s=')
}
