/* eslint-disable import/no-anonymous-default-export */
import {SEARCH_MOVIE, GET_MOVIES} from '../actions/types';

const initialState = {
    text: '',
    movies: [],
    loading: false,
    movie: []
}

export default function(state = initialState, action) {
    switch (action.type) {
        case SEARCH_MOVIE:
            return {
                ...state,
                text: action.payload,
                loading: false
            };
        case GET_MOVIES:
            return {
                ...state,
                movies: action.payload,
                loading: false
            };
        default: 
            return state
    }
}
